import Vue from 'vue';
import Avatar from 'components/Avatar';

export default {
  name: 'Login-Page',
  data() {
    return {
      tokenize: true,
      btn_state: false,
      method: "get",
      message: 'Login',
      pending: false
    }
  },
  methods: {
    _make_basic_auth() {
      var tok = this.$root.user.username + ':' + this.$root.user.password;
      Vue.http.headers.common['Authorization'] = "Basic " + btoa(tok);
    },
    fetch_user(protocol = this.method) {
      this.pending = true;
      this._make_basic_auth();
      this.$http[protocol]('api/u/' + this.$root.user.username, {'params': {'token': this.tokenize}}).then(response => {
        if (response.body.authorized) {
          this.$toast.open({
                      message: 'Welcome ' + this.$root.user.username + "!",
                      type: 'is-success',
                      position: 'is-bottom'
                  });
          this.$root.user.password = '';
          this.$root.user = {...this.$root.user, ...response.body};
          // setTimeout(() => {
            this.$router.push({
              name: 'Profile',
              params: {
                id: this.$root.user.username
              }
            });
          // }, 1000);
        }
        this.pending = false;
      }, error => {
        this.pending = false;
        console.log(error.status + "  " + error.body);
      });
    },
    register() {
      this.fetch_user('post');
    },
    login() {
      this.fetch_user('get');
    },
    valid_username() {
      return this.$root.user.username.length > 0
    },
    valid_password() {
      return this.$root.user.password.length >= 6
    },
    valid() {
      return this.valid_username() && this.valid_password()
    },
    user_exists() {
      this.$http.get('api/u/' + this.$root.user.username, {'params': {'items':['avatar',]}}).then(response => {
        this.message = "Login";
        this.method = "get";
        this.btn_state = false;
        this.$root.user.avatar = response.body.avatar;
      }, error => {
        this.message = "Register";
        this.method = 'post';
        this.btn_state = true;
        this.$root.user.avatar = false;
      });
    }
  },
  updated() { //// FIXME: plz
    this.user_exists();
  },
  components: {
    Avatar
  }
}

import Vue from 'vue'
import Resource from 'vue-resource'
import App from 'App'
import router from 'router/index'
import Buefy from 'buefy'
import 'buefy/lib/buefy.min.css'
// import '@fortawesome/fontawesome-free/css/all.css'
// import 'animate.css/animate.css'
import 'bulma-steps/dist/css/bulma-steps.min.css'
import VuePersist from 'vue-persist'
import VueSocketio from 'vue-socket.io-extended'
import io from 'socket.io-client'


Vue.use(Buefy, {defaultIconPack: 'fas'});
Vue.use(Resource);
Vue.use(VuePersist);
Vue.use(VueSocketio, io('/sock'));

Vue.http.interceptors.push(req => {
  return response => {
    if (response.status == 401) {
      router.push({name: "Login"});
    }
  };
});

const base = {
  token: '',
  authorized: false,
  username: '',
  password: '',
  avatar: '',
  entry: '',
  skill: 0,
  notifications: [],
  arena: {
    // message: '',
    // id: null,
    // start: false,
    // votes: null,
  }
}

new Vue({
  router: router,
  el: '#app',
  delimiters: ["[[", "]]"],
  http: {
    root: 'http://127.0.0.1:5000/'
  },
  components: {
    App
  },
  data() {
    return {
      user: base
    }
  },
  methods: {
    reset_arena() {
      this.user.arena = { };
      this.user.entry = '';
    },
    reset_all() {
      this.user = base;
      this.$router.push({name:'Login'});
    }
  },
  sockets: {
    connect() {
      console.log("connected");
    },
    disconnect() {
      console.log("disconnected");
    }
  },
  watch: {
    'user.token': function(new_token, old) {
      console.log('setting header ' + new_token);
      Vue.http.headers.common['Authorization'] = "Bearer " + new_token;
    }
  },
  persist: ['user'],
  template: '<App/>'
});

import Vue from 'vue';
import Router from 'vue-router';
import Login from 'components/Login'
import Arena from 'components/Arena'
import Main from 'components/Main'
import NotFound from 'components/NotFound'
import Profile from 'components/Profile'

Vue.use(Router);

export default new Router({
  routes: [
    {
      path: '',
      // name: 'Main',
      component: Main,
      children: [
